//CSS
import styles from './EditPost.module.css';

//Hooks
import { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { useAuthValue } from '../../context/AuthContext';
import { useInsertDocument } from '../../hooks/useInsertDocument';
import { useFetchDocument } from '../../hooks/useFetchDocument';

const EditPost = () => {
  const {id} = useParams();
  const { document: post } = useFetchDocument("posts", id);

  const [title, setTitle] = useState("");
  const [image, setImage] = useState("");
  const [body, setBody] = useState("");
  const [tags, setTags] = useState([]);
  const [formError, setFormError] = useState("");

  useEffect(() => {

    if(post) {
      setTitle(post.title);
      setBody(post.body);
      setImage(post.image);

      const textTags = post.tagsArray.join(", ");

      setTags(textTags);
    }

  }, [post]);

  const {user} = useAuthValue();

  const {insertDocument, response} = useInsertDocument("posts");
  
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormError("");

    //validate image URL
    try {
      new URL(image)
    } catch (error) {
      setFormError("A imagem precisa ser uma URL.");
    }

    //create Tag's Array
    const tagsArray = tags.split(",").map((tag) => tag.trim().toLowerCase());

    //Check all values
    if (!title || !image || !tags || !body) {
      setFormError("Por favor, preencha todos os campos!");
    }


    if(formError) return;

    insertDocument({
      title,
      image,
      body,
      tagsArray,
      uid: user.uid,
      createdBy: user.displayName,
    });

    //redirect to home page
    navigate("/");
  };

  return (
    <div className={styles.edit_post}>
        {post && (
          <>
          <h2>Editar Post: {post.title}</h2>
        <p>Edite os dados do post conforme desejar!</p>
        <form onSubmit={handleSubmit}>
          <label>
            <span>Título do Post:</span>
            <input 
              type="text" 
              name="title"
              required 
              placeholder="Pense em um bom título..."
              onChange={(e) => setTitle(e.target.value)}
              value={title} 
            />
          </label>
          <label>
            <span>URL da imagem:</span>
            <input 
              type="text" 
              name="image"
              required 
              placeholder="Insira a imagem que representa o seu post..."
              onChange={(e) => setImage(e.target.value)}
              value={image} 
            />
          </label>
          <p className={styles.preview_title}>Preview da imagem:</p>
          <img className={styles.image_preview} src={post.image} alt={post.title} />
          <label>
            <span>Conteúdo do Post:</span>
            <textarea 
              name="body"
              required
              placeholder="Insira o conteúdo do post..."
              onChange={(e) => setBody(e.target.value)}
              value={body}
              ></textarea>
          </label>
          <label>
            <span>Tags do Post:</span>
            <input 
              type="text" 
              name="tags"
              required 
              placeholder="Insira as tags separadas por virgula,"
              onChange={(e) => setTags(e.target.value)}
              value={tags} 
            />
          </label>
          {!response.loading && <button className='btn'>Editar</button>} 
          {response.loading && <button className='btn' disabled>Aguarde...</button>}
          {response.error && <p className='error'>{response.error}</p>}
          {formError && <p className='error'>{formError}</p>}
        </form>
          </>
        )}
    </div>
  );
};

export default EditPost;
